// frontend/src/CleaningTasksList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function CleaningTasksList() {
  const [tasks, setTasks] = useState([]);
  const [reservations, setReservations] = useState([]);

  // Fetch cleaning tasks and reservations from the backend
  useEffect(() => {
    axios.get('http://localhost:5001/api/cleaning-schedule')
      .then(response => setTasks(response.data))
      .catch(error => console.error('Error fetching cleaning tasks:', error));

    axios.get('http://localhost:5001/api/reservations')
      .then(response => setReservations(response.data))
      .catch(error => console.error('Error fetching reservations:', error));
  }, []);

  // Merge each cleaning task with its corresponding reservation details
  const mergedTasks = tasks.map(task => {
    const reservation = reservations.find(res => res.id === task.reservationId);
    return { ...task, reservation };
  });

  // Handler to mark a cleaning task as completed
  const markTaskCompleted = (taskId) => {
    axios.put(`http://localhost:5001/api/cleaning-schedule/task/${taskId}`, { completed: true })
      .then(response => {
        alert(response.data.message);
        // Refresh the cleaning tasks list
        axios.get('http://localhost:5001/api/cleaning-schedule')
          .then(resp => setTasks(resp.data))
          .catch(err => console.error('Error refreshing cleaning tasks:', err));
      })
      .catch(error => console.error('Error updating task:', error));
  };

  return (
    <div style={{ maxWidth: '900px', margin: 'auto', padding: '20px' }}>
      <h2 style={{ textAlign: 'center', color: '#333' }}>Cleaning Tasks</h2>
      {mergedTasks.length === 0 ? (
        <p style={{ textAlign: 'center' }}>No cleaning tasks found.</p>
      ) : (
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px', justifyContent: 'center' }}>
          {mergedTasks.map(task => (
            <div key={task.taskId} style={{
              border: '1px solid #ccc',
              borderRadius: '8px',
              padding: '15px',
              width: '280px',
              boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
              backgroundColor: '#fff'
            }}>
              <h3 style={{ margin: '0 0 10px 0', color: '#333' }}>Room {task.room}</h3>
              <p style={{ margin: '5px 0' }}>
                <strong>Cleaning Date:</strong> {task.cleaningDate} 11:00 AM
              </p>
              {task.reservation ? (
                <>
                  <p style={{ margin: '5px 0' }}>
                    <strong>Contact:</strong> {task.reservation.customerName}
                  </p>
                  <p style={{ margin: '5px 0' }}>
                    <strong>Phone:</strong> {task.reservation.phone}
                  </p>
                </>
              ) : (
                <p style={{ margin: '5px 0', color: 'red' }}>Reservation info not available</p>
              )}
              <p style={{ margin: '5px 0' }}>
                <strong>Status:</strong> {task.completed ? 'Completed' : 'Pending'}
              </p>
              {!task.completed && (
                <button
                  style={{
                    marginTop: '10px',
                    padding: '8px 16px',
                    backgroundColor: '#28a745',
                    color: '#fff',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer'
                  }}
                  onClick={() => markTaskCompleted(task.taskId)}
                >
                  Mark as Completed
                </button>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default CleaningTasksList;
