// frontend/src/ReservationForm.js
import React, { useState } from 'react';
import axios from 'axios';

function ReservationForm() {
  const [reservation, setReservation] = useState({
    customerName: '',
    rooms: [],
    checkIn: '',
    checkOut: ''
  });

  // Define available hotel rooms
  const hotelRooms = [
    '105', '107', '109',
    '110', '111', '112', '113', '114', '115', '116', '117', '118', '119', '120',
    '205', '206', '207', '208', '209', '210', '211', '212', '213', '214', '215', '216', '217', '218', '219', '220'
  ];

  const handleChange = (e) => {
    setReservation({ ...reservation, [e.target.name]: e.target.value });
  };

  // Handler for the multi-select for rooms
  const handleRoomsChange = (e) => {
    const options = e.target.options;
    const selectedRooms = [];
    for (let i = 0; i < options.length; i++) {
      if (options[i].selected) {
        selectedRooms.push(options[i].value);
      }
    }
    setReservation({ ...reservation, rooms: selectedRooms });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('http://localhost:5001/api/reservations', reservation)
      .then(response => {
        alert('Reservation added successfully!');
        setReservation({
          customerName: '',
          rooms: [],
          checkIn: '',
          checkOut: ''
        });
      })
      .catch(error => {
        console.error('Error adding reservation:', error);
      });
  };

  return (
    <div>
      <h2>Reservation Management</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Customer Name:</label><br />
          <input 
            type="text" 
            name="customerName" 
            value={reservation.customerName} 
            onChange={handleChange} 
            required 
          />
        </div>
        <div>
          <label>Select Room(s):</label><br />
          <select 
            name="rooms" 
            multiple 
            value={reservation.rooms} 
            onChange={handleRoomsChange} 
            size={10} // Shows 10 options at a time; adjust as needed
          >
            {hotelRooms.map((room) => (
              <option key={room} value={room}>
                {room}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label>Check-In Date:</label><br />
          <input 
            type="date" 
            name="checkIn" 
            value={reservation.checkIn} 
            onChange={handleChange} 
            required 
          />
        </div>
        <div>
          <label>Check-Out Date:</label><br />
          <input 
            type="date" 
            name="checkOut" 
            value={reservation.checkOut} 
            onChange={handleChange} 
            required 
          />
        </div>
        <br />
        <button type="submit">Add Reservation</button>
      </form>
    </div>
  );
}

export default ReservationForm;
