// frontend/src/ReservationList.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

function ReservationList() {
  const [reservations, setReservations] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  // Function to fetch reservations from the backend
  const fetchReservations = () => {
    axios.get('http://localhost:5001/api/reservations')
      .then(response => setReservations(response.data))
      .catch(error => console.error('Error fetching reservations:', error));
  };

  useEffect(() => {
    fetchReservations();
  }, []);

  // Filter reservations based on the search term (by customer name or room)
  const filteredReservations = reservations.filter(reservation => {
    const term = searchTerm.toLowerCase();
    return (
      reservation.customerName.toLowerCase().includes(term) ||
      reservation.rooms.join(', ').toLowerCase().includes(term)
    );
  });

  // Handler to delete a reservation
  const handleDelete = (id) => {
    axios.delete(`http://localhost:5001/api/reservations/${id}`)
      .then(response => {
         alert(response.data.message);
         fetchReservations();
      })
      .catch(error => console.error('Error deleting reservation:', error));
  };

  return (
    <div>
      <h2>Reservation List</h2>
      <div style={{ marginBottom: '15px' }}>
        <input
          type="text"
          placeholder="Search reservations by customer or room..."
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
          style={{ width: '300px', padding: '8px' }}
        />
      </div>
      {filteredReservations.length === 0 ? (
         <p>No reservations found.</p>
      ) : (
         <ul>
            {filteredReservations.map(reservation => (
              <li key={reservation.id} style={{ marginBottom: '10px' }}>
                <strong>{reservation.customerName}</strong> - Rooms: {reservation.rooms.join(', ')} - CheckIn: {reservation.checkIn} - CheckOut: {reservation.checkOut}
                &nbsp;
                <button onClick={() => handleDelete(reservation.id)}>Delete</button>
                &nbsp;
                <button onClick={() => window.location.href = `/edit-reservation/${reservation.id}`}>Edit</button>
              </li>
            ))}
         </ul>
      )}
    </div>
  );
}

export default ReservationList;
