// frontend/src/HousekeepingDashboard.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Bar } from 'react-chartjs-2';

// Register Chart.js components
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

function HousekeepingDashboard() {
  const [cleaningData, setCleaningData] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5001/api/cleaning-schedule')
      .then(response => setCleaningData(response.data))
      .catch(error => console.error('Error fetching cleaning schedule:', error));
  }, []);

  // Group cleaning tasks by cleaningDate
  const groupedData = cleaningData.reduce((acc, task) => {
    const date = task.cleaningDate;
    if (acc[date]) {
      acc[date]++;
    } else {
      acc[date] = 1;
    }
    return acc;
  }, {});

  // Prepare labels and counts for the chart
  const labels = Object.keys(groupedData);
  const counts = Object.values(groupedData);

  const data = {
    labels: labels.length > 0 ? labels : ['No Data'],
    datasets: [
      {
        label: 'Cleaning Tasks',
        data: counts.length > 0 ? counts : [0],
        backgroundColor: 'rgba(75,192,192,0.4)',
      }
    ]
  };

  return (
    <div>
      <h2>AFC Housekeeping Ministry Chart</h2>
      <Bar data={data} />
    </div>
  );
}

export default HousekeepingDashboard;
