// frontend/src/ReservationList.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function ReservationList() {
  const [reservations, setReservations] = useState([]);
  const navigate = useNavigate();

  // Function to fetch reservations from the backend
  const fetchReservations = () => {
    axios.get('http://localhost:5001/api/reservations')
      .then(response => setReservations(response.data))
      .catch(error => console.error('Error fetching reservations:', error));
  };

  useEffect(() => {
    fetchReservations();
  }, []);

  // Sort reservations by checkIn (assuming checkIn is an ISO datetime string)
  const sortedReservations = reservations.slice().sort((a, b) => {
    return new Date(a.checkIn) - new Date(b.checkIn);
  });

  // Handler for deleting a reservation
  const handleDelete = (id) => {
    if (window.confirm("Are you sure you want to delete this reservation?")) {
      axios.delete(`http://localhost:5001/api/reservations/${id}`)
        .then(response => {
          alert(response.data.message);
          fetchReservations();
        })
        .catch(error => console.error('Error deleting reservation:', error));
    }
  };

  // Handler for editing a reservation; now it navigates to the current version
  // of reservation management with a query parameter indicating the reservation to edit.
  const handleEdit = (id) => {
    navigate(`/reservations?editId=${id}`);
  };

  return (
    <div style={{ maxWidth: '900px', margin: 'auto', padding: '20px' }}>
      <h2 style={{ textAlign: 'center', color: '#333' }}>Reservation List</h2>
      {sortedReservations.length === 0 ? (
        <p style={{ textAlign: 'center' }}>No reservations found.</p>
      ) : (
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px', justifyContent: 'center' }}>
          {sortedReservations.map(reservation => (
            <div key={reservation.id} style={{
              border: '1px solid #ccc',
              borderRadius: '8px',
              padding: '20px',
              width: '280px',
              boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
              backgroundColor: '#fff'
            }}>
              <h3 style={{ margin: '0 0 10px', color: '#007BFF' }}>{reservation.customerName}</h3>
              <p style={{ margin: '5px 0' }}>
                <strong>Email:</strong> {reservation.email}
              </p>
              <p style={{ margin: '5px 0' }}>
                <strong>Phone:</strong> {reservation.phone}
              </p>
              <p style={{ margin: '5px 0' }}>
                <strong>Rooms:</strong> {reservation.rooms.join(', ')}
              </p>
              <p style={{ margin: '5px 0' }}>
                <strong>Check-In:</strong> {reservation.checkIn}
              </p>
              <p style={{ margin: '5px 0' }}>
                <strong>Check-Out:</strong> {reservation.checkOut}
              </p>
              <div style={{ marginTop: '15px', display: 'flex', justifyContent: 'space-between' }}>
                <button 
                  onClick={() => handleEdit(reservation.id)}
                  style={{
                    padding: '8px 12px',
                    backgroundColor: '#FFC107',
                    color: '#fff',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer'
                  }}
                >
                  Edit
                </button>
                <button 
                  onClick={() => handleDelete(reservation.id)}
                  style={{
                    padding: '8px 12px',
                    backgroundColor: '#DC3545',
                    color: '#fff',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer'
                  }}
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default ReservationList;
