// frontend/src/EditReservation.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

function EditReservation() {
  const { id } = useParams(); // get the reservation ID from the URL
  const navigate = useNavigate();
  const [reservation, setReservation] = useState({
    customerName: '',
    rooms: [],
    checkIn: '',
    checkOut: ''
  });

  // Fetch the reservation data by ID
  useEffect(() => {
    axios.get('http://localhost:5001/api/reservations')
      .then(response => {
        const found = response.data.find(r => r.id === parseInt(id, 10));
        if (found) {
          setReservation(found);
        } else {
          alert('Reservation not found!');
          navigate('/reservation-list');
        }
      })
      .catch(error => console.error('Error fetching reservation:', error));
  }, [id, navigate]);

  const handleChange = (e) => {
    setReservation({ ...reservation, [e.target.name]: e.target.value });
  };

  const handleRoomsChange = (e) => {
    const options = e.target.options;
    const selectedRooms = [];
    for (let i = 0; i < options.length; i++) {
      if (options[i].selected) {
        selectedRooms.push(options[i].value);
      }
    }
    setReservation({ ...reservation, rooms: selectedRooms });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.put(`http://localhost:5001/api/reservations/${id}`, reservation)
      .then(response => {
        alert(response.data.message);
        navigate('/reservation-list');
      })
      .catch(error => console.error('Error updating reservation:', error));
  };

  const hotelRooms = [
    '105', '107', '109',
    '110', '111', '112', '113', '114', '115', '116', '117', '118', '119', '120',
    '205', '206', '207', '208', '209', '210', '211', '212', '213', '214', '215', '216', '217', '218', '219', '220'
  ];

  return (
    <div>
      <h2>Edit Reservation</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Customer Name:</label><br />
          <input 
            type="text" 
            name="customerName" 
            value={reservation.customerName} 
            onChange={handleChange} 
            required 
          />
        </div>
        <div>
          <label>Select Room(s):</label><br />
          <select 
            name="rooms" 
            multiple 
            value={reservation.rooms} 
            onChange={handleRoomsChange} 
            size={10}
          >
            {hotelRooms.map(room => (
              <option key={room} value={room}>
                {room}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label>Check-In Date:</label><br />
          <input 
            type="date" 
            name="checkIn" 
            value={reservation.checkIn} 
            onChange={handleChange} 
            required 
          />
        </div>
        <div>
          <label>Check-Out Date:</label><br />
          <input 
            type="date" 
            name="checkOut" 
            value={reservation.checkOut} 
            onChange={handleChange} 
            required 
          />
        </div>
        <br />
        <button type="submit">Update Reservation</button>
      </form>
    </div>
  );
}

export default EditReservation;
