// frontend/src/HousekeepingDashboard.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Bar } from 'react-chartjs-2';

// Register the necessary Chart.js components
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

function HousekeepingDashboard() {
  const [cleaningData, setCleaningData] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5001/api/cleaning-schedule')
      .then(response => setCleaningData(response.data))
      .catch(error => console.error('Error fetching cleaning schedule:', error));
  }, []);

  // For demonstration, we use dummy data for the chart.
  const data = {
    labels: ['Week 1', 'Week 2', 'Week 3', 'Week 4'],
    datasets: [
      {
        label: 'Cleaning Tasks',
        data: [12, 19, 3, 5],
        backgroundColor: 'rgba(75,192,192,0.4)',
      }
    ]
  };

  return (
    <div>
      <h2>AFC Housekeeping Ministry Chart</h2>
      <Bar data={data} />
    </div>
  );
}

export default HousekeepingDashboard;
