// frontend/src/UpcomingCleaningTasks.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function UpcomingCleaningTasks() {
  const [tasks, setTasks] = useState([]);

  // Fetch cleaning tasks from the backend when the component mounts
  useEffect(() => {
    axios.get('http://localhost:5001/api/cleaning-schedule')
      .then(response => setTasks(response.data))
      .catch(error => console.error('Error fetching cleaning tasks:', error));
  }, []);

  // Calculate the target date (exactly one week from today)
  const today = new Date();
  const targetDate = new Date(today);
  targetDate.setDate(today.getDate() + 7);
  const targetDateStr = targetDate.toISOString().slice(0, 10);

  // Filter tasks that are scheduled for the target date
  const upcomingTasks = tasks.filter(task => task.cleaningDate === targetDateStr);

  return (
    <div style={{ maxWidth: '800px', margin: 'auto', padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h2 style={{ textAlign: 'center', color: '#333' }}>
        Upcoming Cleaning Tasks for {targetDateStr} (One Week from Today)
      </h2>
      {upcomingTasks.length === 0 ? (
        <p style={{ textAlign: 'center' }}>No cleaning tasks scheduled for {targetDateStr}.</p>
      ) : (
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px', justifyContent: 'center' }}>
          {upcomingTasks.map(task => (
            <div key={task.taskId} style={{
              border: '1px solid #ccc',
              borderRadius: '8px',
              padding: '15px',
              width: '280px',
              boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
              backgroundColor: '#fff'
            }}>
              <h3 style={{ margin: '0 0 10px', color: '#007BFF' }}>Room {task.room}</h3>
              <p style={{ margin: '5px 0' }}>
                <strong>Date:</strong> {task.cleaningDate} 11:00 AM
              </p>
              <p style={{ margin: '5px 0' }}>
                <strong>Status:</strong> {task.completed ? 'Completed' : 'Pending'}
              </p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default UpcomingCleaningTasks;
